import NavbarO from "./NavbarO";
import "./Find.css";





function FindMovers(){
    
const data = [
    {
      name: '🍌 banana',
      color: "yellow"
    },
    {
      name: '🍊 orange',
      color: "orange"
    },
    {
      name: '🍎 apple',
      color: "#9a464c"
    },
    {
      name: '🍇 grape',
      color: "#6f2da8"
    },
    {
      name: '🍉 watermelon',
      color: "#e1697c"
    },
    {
      name: '🥥 coco',
      color: "white"
    }
  ]
  
  const search = document.getElementById('search')
  const list = document.getElementById('list')
  
  //get input
  search.addEventListener('keyup', (event) => {
    const imputText = event.target.value
    const filteredData = data.filter(item => {
      return (
        item.name.toLowerCase().includes(imputText.toLowerCase())
      )
    })
    renderList(filteredData)
  })
  
  
  const renderList = (data) => {  
    
    (data.length == 1)? 
    document.body.style.backgroundColor = data[0].color : 
    document.body.style.backgroundColor= "cyan"
 
    list.innerHTML = data.map(item => `<li>${item.name}</li>`).join('')
  }
  
  
  
    return(
        <>
        renderList(data)
            <NavbarO />
            <div class="box">
                <input type="text" id='search' placeholder="🔎 Search here..." />
                <ul id="list"></ul>
            </div>
        </>
    )
}

export default FindMovers;
